import type { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import UsuariosController from 'App/Controllers/Http/UsuariosController'

export default class AuthJwt {
  public async handle(ctx: HttpContextContract, next: () => Promise<void>) {
    const authorizationHeader = ctx.request.header('authorization')

    if(authorizationHeader == undefined){
      return ctx.response.status(400).send({
        mensaje: "Falta el token de autorización",
        estado: 401,
      })
    }

    try{
      const usuariosController = new UsuariosController()
      usuariosController.verificarToken(authorizationHeader)
      await next()
    }catch(error){            
      console.log(error);
      ctx.response.status(400).send("Falla relacionada con el token")
    }
  }
}

import type { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import Libro from 'App/Models/Libro';

export default class LibrosController {
    public async registrar({request, response}: HttpContextContract){
        const {titulo, isbn, editorialId, autorId} = request.all();
        const libro = new Libro();
        libro.titulo = titulo;
        libro.isbn = isbn;
        libro.id_editorial = editorialId;
        libro.id_autor = autorId;        
        await libro.save();
        return{libro, "msj": "Libro registrado"}
    }

    public async getListarLibros(): Promise<Libro[]> {
        const libros = await Libro.all();
        return libros;
    }
}

